import { Names, PdfDictionary } from "../../../syntax";

export type FilterName = Names.ASCIIHexDecode | Names.ASCII85Decode | Names.LZWDecode | Names.FlateDecode | Names.RunLengthDecode | Names.DCTDecode;

export interface IFilter {
  name: FilterName;

  /**
   * Кодирует входящие данные по параметрам.
   * @param data Входящие данные.
   * @param params Параметры кодирования.
   */
  encode(data: Uint8Array, params: PdfDictionary | null): Uint8Array;
  /**
   * Декодирует входящие данные по параметрам.
   * @param data Входные данные.
   * @param params Параметры декодирования.
   */
  decode(data: Uint8Array, params: PdfDictionary | null): Uint8Array;
}
