import { CustomFont } from "./custom_font";

/**
 * Класс Fonts предоставляет методы для работы с шрифтами в PDF.
 */
export class Fonts {
  /**
   * Создает экземпляр пользовательского шрифта на основе данных шрифта в формате Uint8Array.
   * @param {Uint8Array} fontData - Данные шрифта в формате Uint8Array.
   * @returns {CustomFont} Экземпляр пользовательского шрифта.
   */
  public static getCustom(fontData: Uint8Array): CustomFont {
    return new CustomFont({ fontData });
  }
}
