import { IPdfFlagsParams, PdfFlags } from "./flags";

export class PdfAnnotFlags extends PdfFlags<typeof AnnotFlags>{
  public constructor(options?: IPdfFlagsParams<typeof AnnotFlags>) {
    super(AnnotFlags, options);
  }
}

export enum AnnotFlags {
  Invisible = 1 << 0,
  Hidden = 1 << 1,
  Print = 1 << 2,
  NoZoom = 1 << 3,
  NoRotate = 1 << 4,
  NoView = 1 << 5,
  ReadOnly = 1 << 6,
  Locked = 1 << 7,
  ToggleNoView = 1 << 8,
  LockedContents = 1 << 9,
}

export enum AnnotFlagsName {
  Invisible = "Invisible",
  Hidden = "Hidden",
  Print = "Print",
  NoZoom = "NoZoom",
  NoRotate = "NoRotate",
  NoView = "NoView",
  ReadOnly = "ReadOnly",
  Locked = "Locked",
  ToggleNoView = "ToggleNoView",
  LockedContents = "LockedContents",
}
