import { IPdfObjectParams, PdfObject } from "./object";

export interface IPdfBooleanParams extends IPdfObjectParams {
  value?: boolean;
}

export class PdfBoolean extends PdfObject {
  #value: boolean;

  public constructor(param: IPdfBooleanParams = {}) {
    super(param);
    this.#value = param.value ?? false;
  }

  public get value(): boolean {
    return this.#value;
  }

  public set value(v: boolean) {
    if (v !== this.#value) {
      this.change();
      this.#value = v;
    }
  }
}
