import { IPdfObjectParams, PdfObject } from "./object";

export interface IPdfCommentParams extends IPdfObjectParams {
  value?: string;
}

/**
 * Комментарий. Представляет собой строку, которая начинается с символа % и
 * заканчивается переводом строки.
 */
export class PdfComment extends PdfObject {

  #value = "";

  public constructor(param: IPdfCommentParams = {}) {
    super(param);

    if (param.value) {
      this.value = param.value;
    }
  }

  public get value(): string {
    return this.#value;
  }

  public set value(v: string) {
    if (v !== this.value) {
      this.change();
      this.#value = v;
    }
  }
}
