import { PdfObject } from "./object";
import { IPdfStringParams } from "./string";

export class PdfName extends PdfObject {
  #value: string;

  public constructor(param: IPdfStringParams = {}) {
    super(param);
    this.#value = param.value ?? "";
  }

  public get value(): string {
    return this.#value;
  }

  public set value(v: string) {
    if (v !== this.#value) {
      this.change();
      this.#value = v;
    }
  }
}
