import { PdfObject } from "./object";
import { RawData } from "./raw_data";

export interface IPdfOperatorParams {
  tag: string;
  arguments?: PdfObject[];
}

export class PdfOperator extends RawData {
  public tag: string;
  public params: PdfObject[];

  public constructor(param: IPdfOperatorParams) {
    super();

    this.tag = param.tag;
    this.params = param.arguments || [];
  }
}
